mapper.select:
select post_code as postCode, user_code as userCode, content_code as contentCode, techblog_code as techblogCode, post_text as postText, post_like as postLike, post_comment as postComment, post_date as postDate, post_scrap as postScrap, post_edit as postEdit from post p
where post_code in
#{item}
and (p.post_code < #{lastPostCode})
order by p.post_code desc limit #{size}

select post_code
from postscrap
where user_code = #{userCode}
