mapper.update:
update content
set
content_scrap
= content_scrap + #{count}
where content_code = #{cid}

update content
set
content_like =
content_like + #{count}
where content_code = #{cid}

update content
set
content_read =
content_read + #{count}
where content_code = #{cid}

mapper.select:
select content_code `contentCode`, techblog_code `techblogCode`, content_title `contentTitle`, content_text `contentText`, content_url `contentUrl`, content_img `contentImg`, content_date `contentDate`, content_keyword `contentKeyword` from content where (content_title like concat('%',#{search},'%') or content_text like concat('%',#{search},'%'))
and(
content_keyword like concat('%',#{item},'%')
)
and ((content_code < #{lastContentCode}))
limit #{size}

select content_code `contentCode`, techblog_code `techblogCode`, content_title `contentTitle`, content_text `contentText`, content_url `contentUrl`, content_img `contentImg`, content_date `contentDate`, content_keyword `contentKeyword`,
CONCAT(DATE_FORMAT(content_date, '%y%m%d'), LPAD(content_code, 10,0)) as cur from content
where techblog_code = #{techblogCode}
having cur < #{cursor}
order by content_date desc, content_code desc limit #{size}
