mapper.select:
select post_code as postCode, user_code as userCode, content_code as contentCode, techblog_code as techblogCode, post_text as postText, post_like as postLike, post_comment as postComment, post_date as postDate, post_scrap as postScrap, post_edit as postEdit from post p
where (p.user_code in
#{item}
)
and (p.post_code < #{lastPostCode})
order by p.post_code desc limit #{size}

select post_code as postCode, user_code as userCode, content_code as contentCode, techblog_code as techblogCode, post_text as postText, post_like as postLike, post_comment as postComment, post_date as postDate, post_scrap as postScrap, post_edit as postEditdit from post where user_code = #{userCode}
and (post_code < #{lastPostCode})
order by post_code desc limit #{size}

mapper.update:
update post
set post_text = #{postText}, post_edit = 1
where post_code =#{postCode}

update post
set post_scrap
= post_scrap + #{count}
where post_code = #{pid}

update post
set post_like =
post_like + #{count}
where post_code = #{pid}

update post
set
post_comment = post_comment + #{count}
where post_code = #{postCode}

mapper.delete:
delete from postlike
where
post_code = #{pid} and user_code=#{uid}

delete from postscrap	
	where
	post_code = #{pid} and user_code=#{uid}
