mapper.update:
update user
set
user_keyword = #{userkeyword}
where user_code= #{usercode}

update user
set
user_intro = #{userIntro},
user_img = #{userImg}
where user_code=
#{userCode}

mapper.select:

select user_code `userCode`
from user
where user_email = #{userEmail}

mapper.delete:
delete from follow
where
user_code = #{from} and following_code=#{to}

mapper.select:
select u.user_id `userId`, u.user_nick `userNick`, u.user_img `userImg`, u.user_postcount `userPostCount`, u.user_followercount `userFollowerCount`, u.user_followingcount `userFollowingCount`, u.user_intro `userIntro`, u.user_keyword `userKeyword`, case when exists (select * from follow f where f.following_code = u.user_code and f.user_code = #{userCode}) then true else false end `isFollow` from user u where (u.user_nick like concat('%',#{search},'%') or u.user_intro like concat('%',#{search},'%')) and u.user_code != #{userCode}
and (post_code < #{lastPostCode})
limit #{size}

select user_code `userCode`, user_id `userId`, user_nick `userNick`, user_img `userImg` from user where user_code not in
#{item}
and(
user_keyword like concat('%',#{item},'%')
)
order by rand() limit #{count}

select user_code `userCode`, user_id `userId`, user_nick `userNick`, user_img `userImg` from user where user_code not in
#{item}
order by rand() limit #{count}
